    const sensoresGigantes = ["Volumen FA-02", "Flujo Materia Prima", "Salida Calentador"];
    const sensoresGrandes = ["Presin Alimentacin", "Salida HCD"];
    const sensoresMedianos = ["Entrada Calentador", "Salida Asfalto"];

    let estiloBase = "text-align: right; color: #666666;";

    // Estilos base segn la importancia del sensor
    if (sensoresGigantes.includes(nombre)) {
        return estiloBase + " font-size: 24px; padding: 16px 8px;";
    } else if (sensoresGrandes.includes(nombre)) {
        return estiloBase + " font-size: 20px; padding: 12px 8px;";
    } else if (sensoresMedianos.includes(nombre)) {
        return estiloBase + " font-size: 18px; padding: 10px 8px;";
    } else {
        return estiloBase + " font-size: 16px; padding: 8px;";
    }
}

const exec = require('child_process').exec;

