    return null; // No hacer nada si el usuario cancela
}

// Recuperar la accin original (shutdown o reboot) del topic
const accion = msg.topic;

// Determinar el comando segn el sistema operativo
let comando;
const plataforma = process.platform;

// Definir el comando segn el sistema operativo
if (plataforma === "win32") { // Windows
    if (accion === "shutdown") {
        comando = 'shutdown /s /t 15'; // Apagar con 15 segundos de retraso
    } else if (accion === "reboot") {
        comando = 'shutdown /r /t 15'; // Reiniciar con 15 segundos de retraso
    }
} else if (plataforma === "linux" || plataforma === "darwin") { // Linux o macOS
    if (accion === "shutdown") {
        comando = 'sudo shutdown -h now'; // Apagar inmediatamente
    } else if (accion === "reboot") {
        comando = 'sudo reboot'; // Reiniciar inmediatamente
    }
}

// Ejecutar el comando si se ha definido
if (comando) {
    // Mostrar mensaje de aviso
    node.warn(`Ejecutando comando: ${comando}`);

    // Ejecutar el comando
    exec(comando, (error, stdout, stderr) => {
        if (error) {
            node.error(`Error al ejecutar el comando: ${error}`);
            return;
        }
    });

    // Informar al usuario
    const accionTexto = accion === "shutdown" ? "apagando" : "reiniciando";
    msg.payload = `Sistema ${accionTexto} en 15 segundos...`;
